import React from 'react';
import './ExpenseForm.css';
const topicChangeHandler = ()=>{console.log('topic change event!')}
const amoutChangeHandler = () =>{console.log('amount change event!')}
const dateChangeHandler = () =>{console.log('date change event!')}


const ExpenseForm =()=>{return(
<form>
        <div className="new-expense__controls">
            <div className="new-expense__control">
                <label>Topic</label>
                <input type='text' onChange={topicChangeHandler}/>
            </div>
            <div className="new-expense__control">
                <label>Amout</label>
                <input type='number' min="0.01" step="0.01" onChange={amoutChangeHandler}/>
            </div>
            <div className="new-expense__control">
                <label>Date</label>
                <input type='date' min="2019-01-01" max="2022-12-31" onChange={dateChangeHandler} />
            </div>       

        </div>
        <div className="new-expense__actions">
                <button type='submit'>new form</button>
            </div>
    </form>
)}

export default ExpenseForm;
